import React, { Component } from 'react'
import TransactionService from '../../services/TransactionService'

class ListTransactionComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            transactions: []
        }
        this.addTransaction = this.addTransaction.bind(this);
        // this.editBankAccount = this.editBankAccount.bind(this);
        // this.deleteBankAccount = this.deleteBankAccount.bind(this);
    }

    // deleteBankAccount(id){
    //     BankAccountService.deleteBankAccount(id).then( res => {
    //         this.setState({bankAccounts: this.state.bankAccounts.filter(bankAccount => bankAccount.id !== id)});
    //     });
    // }
    // viewBankAccount(id){
    //     this.props.history.push(`/view-bank-account/${id}`);
    // }
    // editBankAccount(id){
    //     this.props.history.push(`/add-bank-account/${id}`);
    // }

    componentDidMount(){
        TransactionService.getAllTransactions().then((res) => {
            this.setState({ transactions: res.data});
        });
    }

    addTransaction(){
        this.props.history.push('/add-transaction');
    }

    render() {
        return (
            <div>
                 <h2 className="text-center">Transaction List</h2>
                 <div className = "row">
                    <button className="btn btn-primary" onClick={this.addTransaction}> Add Transaction</button>
                 </div>
                 <br></br>
                 <div className = "row">
                        <table className = "table table-striped table-bordered">

                            <thead>
                                <tr>
                                    <th> Bank Account Name</th>
                                    <th> Transaction Amount</th>
                                    <th> Transaction Type</th>
                                    <th> Transaction Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.transactions.map(
                                        transaction => 
                                        <tr key = {transaction.id}>
                                             <td> {transaction.bankName} </td>
                                             <td> {transaction.amount}</td>
                                             <td> {transaction.type}</td>
                                             <td> {transaction.status}</td>
                                             {/* <td>
                                                 <button onClick={ () => this.editBankAccount(bankAccount.id)} className="btn btn-info">Update </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.deleteBankAccount(bankAccount.id)} className="btn btn-danger">Delete </button>
                                                 <button style={{marginLeft: "10px"}} onClick={ () => this.viewBankAccount(bankAccount.id)} className="btn btn-info">View </button>
                                             </td> */}
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>

                 </div>

            </div>
        )
    }
}

export default ListTransactionComponent
