import React, { Component } from 'react'
import TransactionService from '../../services/TransactionService'
import { makeStyles } from '@material-ui/core/styles';
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
    },
    heading: {
      fontSize: theme.typography.pxToRem(15),
      fontWeight: theme.typography.fontWeightRegular,
    },
  }));

class ListTransactionComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            transactions: []
        }
        this.addTransaction = this.addTransaction.bind(this);
        this.goBankAccountList = this.goBankAccountList.bind(this);
    }

    componentDidMount(){
        TransactionService.getAllTransactions().then((res) => {
            this.setState({ transactions: res.data});
        });
    }

    addTransaction(){
        this.props.history.push('/add-transaction');
    }

    goBankAccountList(){
        this.props.history.push('/bankAccounts');
    }

    render() {
        const classes = useStyles();
        return (
            <div>
                <h2 className="text-center">Transaction List</h2>
                <div className = "row">
                    <button className="btn btn-primary" onClick={this.addTransaction}> Add Transaction</button>
                    <button style={{marginLeft: "10px"}} className="btn btn-primary" onClick={this.goBankAccountList}> Bank Account List</button>
                </div>
                <br></br>
                <div className = "row">
                    {
                        this.state.transactions.map(
                            transaction => 
                            <Accordion key = {transaction.id}>
                                <AccordionSummary aria-controls="panel1a-content" id="panel1a-header">
                                    <Typography className={classes.heading}>Transaction {transaction.id}</Typography>
                                </AccordionSummary>
                                <AccordionDetails>
                                <Typography>
                                    <li>Bank Name: {transaction.bankName}</li>
                                    <li>Transaction Amount: {transaction.amount}</li>
                                    <li>Transaction Type: {transaction.type}</li>
                                    <li>Transaction Status: {transaction.status}</li>
                                </Typography>
                                </AccordionDetails>
                            </Accordion>
                        )
                    }
                </div>
            </div>
        )
    }
}

export default ListTransactionComponent
